import React, { useState,useEffect,useHistory } from 'react';
import back from '../Img/back.jpg';
import Header from '../Components/Header';
import {
    Form,
    Button,
    Nav,
  } from "react-bootstrap";

  export default function Register(){

    const [login, setLogin] = useState();
    const [password, setPassword]= useState();
    const [values, setValues] = useState({ password: "", showPassword: false });
    const [form, setForm] = useState({
    username: "",
    email: "",
    password: "",
  });
    const [errMsg, setErrMsg] = useState("none");
  const handleClickShowPassword = () => {
    setValues({ ...values, showPassword: !values.showPassword });
  };
  function handleMouseDownPassword(e)  {
    e.preventDefault();
  }
  function onUserNameChange(e)  {
  setForm({
    username: e.target.value,
    email: form.email,
    password: form.password,
  });
}
  function onEmailChange(e) {
  setForm({
    username: form.unsername,
    email: e.target.value,
    password: form.password,

  });
}

  function onPassChange(e) {
  setForm({
    username: form.username,
    email: form.email,
    password: e.target.value,

  });
}


const history = useHistory();

function onSubForm(e){
  if (
    form.username !== "" &&
    form.email !== "" &&
    form.password !== ""
  ) {
    const usersFromLocalStorage = localStorage.getItem("data");

    if (usersFromLocalStorage) {
      const parsedQuestions = JSON.parse(usersFromLocalStorage);
      parsedQuestions.push(form);
      localStorage.setItem("data", JSON.stringify(parsedQuestions));
    } else {
      localStorage.setItem("data", JSON.stringify([form]));
    }

    // history.push({
    //   pathname: "/login",
    //   search: "?reg=success"
    // });
  } else {
    setErrMsg("block");
  }
  e.preventDefault();
};

// useEffect(() => {
//   if (localStorage.getItem("auth") === "true")
//     history.push({ pathname: "/admin" });
// }, []);

    return (
        <div>
        <Header/>
        <Form
         onSubmit={onSubForm} 
         noValidate 
         className="FormReg">
        <Form.Label>Username</Form.Label>
        <Form.Control 
        value={form.username}
        onChange={onUserNameChange}
        type="text"
        placeholder="Enter username" />
    <Form.Text className="text-muted">
      We'll never share your email with anyone else.
    </Form.Text>
  <Form.Group controlId="formBasicEmail">
    <Form.Label>Email address</Form.Label>
    <Form.Control 
    type="email" 
    value={form.email}
    onChange={onEmailChange}
    placeholder="Enter email" />
    <Form.Text className="text-muted">
      We'll never share your email with anyone else.
    </Form.Text>

  </Form.Group>

  <Form.Group controlId="formBasicPassword">
    <Form.Label>Password</Form.Label>
    <Form.Control 
    type="password" 
    value={form.password}
    onChange={onPassChange}

    placeholder="Password" />
  </Form.Group>
  <Form.Group controlId="formBasicCheckbox">
    <Form.Check type="checkbox" label="Check me out" />
  </Form.Group>
  <Button 
  variant="primary" 

  type="submit">
    Submit
  </Button>
</Form>
    </div>
    );
}
