import React, { useState,useEffect } from 'react';
import Header from '../Components/Header';
import {
    Form,
    Button,
    Nav,
  } from "react-bootstrap";
  import { useHistory, Link } from "react-router-dom";



  function Login() {
    const [errMsg, SetErrMsg] = useState("none");
    const [values, setValues] = useState({ password: "", showPassword: false });
    const [email, setEmail] = useState("");
    const [password, setPassord] = useState("");


    const handleClickShowPassword = () => {
      setValues({ ...values, showPassword: !values.showPassword });
    };

    const handleMouseDownPassword = (event: any) => 
    event.preventDefault();
    const onEmailChange = (e: any) => 
    setEmail(e.target.value);
    const onPasswordChange = (e: any) => 
    setPassord(e.target.value);

    const history = useHistory();
    const qparse: any = queryString.parse(history.location.search);

    const onSubmitForm = (e: any) => {
      const usersLocalStorage = localStorage.getItem("data");
      if (usersLocalStorage) {
        const parse = JSON.parse(usersLocalStorage);
        const existUser = parse.find(
          (data: any) => data.email === email && data.password === password
        );
        if (!existUser) {
          localStorage.setItem("auth", "false");
          SetErrMsg("block");
        } else {
          localStorage.setItem("auth", "true");
          localStorage.setItem("authUser", JSON.stringify(existUser));
          history.push({
            pathname: "/admin"
          });
        }
      }
      e.preventDefault();
    };

    useEffect(() => {
      if (qparse.auth === "false") {
        SetErrMsg("block");
      }
      if (localStorage.getItem("auth") === "true")
        history.push({ pathname: "/admin" });
    }, []);


    return(
        <div>

        </div>
    );
  }
  export default Login;
