import React from 'react';

import Header from '../Components/Header';


import {
    Form,
    Button,

  } from "react-bootstrap";
function Alsham(){

    function onFormSubmit(e) {
        e.preventDefault();
        const nameValue = document.querySelector('#name').value;
        const commentValue = document.querySelector('#comment').value;
        const listElem = document.querySelector('#list');   
        const formElem = document.querySelector('#form');

        const liElem = document.createElement('li');
        liElem.id = `${nameValue}: ${commentValue}`;
        liElem.appendChild(document.createTextNode(`Name : ${nameValue}, Comment : ${commentValue}`));
        listElem.appendChild(liElem);
        formElem.reset();

    }


    return (
        <div>






        <div className="comAlsham">
           <h3 className="commentText">Комментарии</h3> 
           <Form 

            className="FormComments">
  <Form.Group controlId="formBasicEmail">
    <Form.Label>Name</Form.Label>
    <Form.Control type="text" placeholder="enter your name" />
    <Form.Text className="text-muted">
      We'll never share your email with anyone else.
    </Form.Text>
  </Form.Group>

  <Form.Group controlId="exampleForm.ControlTextarea1">
    <Form.Label>Example textarea</Form.Label>
    <Form.Control as="textarea" rows="3" />
  </Form.Group>

  <Button 
   onSubmit={onFormSubmit} 
  variant="primary"
   type="submit">
    Submit
  </Button>
</Form>

        </div>
	</div>


    );
}

export default Alsham;
