import React, { useState } from 'react';
import back from '../Img/back.jpg';
import Header from '../Components/Header';
import {
    Form,
    Button,
    Nav,
  } from "react-bootstrap";

function Register(){

    const [login, setLogin] = useState();
    const [password, setPassword]= useState();
    const [values, setValues] = useState({ password: "", showPassword: false });
    const [form, setForm] = useState({
    username: "",
    email: "",
    password: "",
  });
    const [errMsg, setErrMsg] = useState("none");
  const handleClickShowPassword = () => {
    setValues({ ...values, showPassword: !values.showPassword });
  };
  function handleMouseDownPassword(e)  {
    e.preventDefault();
  }

  const onNameChange = (e: any) =>
  setForm({
    name: e.target.value,
    email: form.email,
    password: form.password,
    gender: form.gender
  });
const onEmailChange = (e: any) =>
  setForm({
    name: form.name,
    email: e.target.value,
    password: form.password,
    gender: form.gender
  });
const onPassChange = (e: any) =>
  setForm({
    name: form.name,
    email: form.email,
    password: e.target.value,
    gender: form.gender
  });
const onGenderChange = (e: any) =>
  setForm({
    name: form.name,
    email: form.email,
    password: form.password,
    gender: e.target.value
  });

const history = useHistory();

const onSubForm = (e: any) => {
  if (
    form.name !== "" &&
    form.email !== "" &&
    form.password !== "" &&
    form.gender !== ""
  ) {
    const usersFromLocalStorage = localStorage.getItem("data");

    if (usersFromLocalStorage) {
      const parsedQuestions = JSON.parse(usersFromLocalStorage);
      parsedQuestions.push(form);
      localStorage.setItem("data", JSON.stringify(parsedQuestions));
    } else {
      localStorage.setItem("data", JSON.stringify([form]));
    }

    history.push({
      pathname: "/login",
      search: "?reg=success"
    });
  } else {
    setErrMsg("block");
  }
  e.preventDefault();
};

useEffect(() => {
  if (localStorage.getItem("auth") === "true")
    history.push({ pathname: "/admin" });
}, []);

    return (
        <div>
        <Header/>
        <Form className="FormReg">
        <Form.Label>Username</Form.Label>
        <Form.Control type="text" placeholder="Enter username" />
    <Form.Text className="text-muted">
      We'll never share your email with anyone else.
    </Form.Text>
  <Form.Group controlId="formBasicEmail">
    <Form.Label>Email address</Form.Label>
    <Form.Control type="email" placeholder="Enter email" />
    <Form.Text className="text-muted">
      We'll never share your email with anyone else.
    </Form.Text>

  </Form.Group>

  <Form.Group controlId="formBasicPassword">
    <Form.Label>Password</Form.Label>
    <Form.Control type="password" placeholder="Password" />
  </Form.Group>
  <Form.Group controlId="formBasicCheckbox">
    <Form.Check type="checkbox" label="Check me out" />
  </Form.Group>
  <Button variant="primary" type="submit">
    Submit
  </Button>
</Form>
    </div>
    );
}

export default Register;
